		$icons = apply_filters('wp_hero_icons', $icons);
		
		// Allow filtering of specific icon groups
		$icons[$group] = apply_filters("wp_hero_{$group}_icons", $icons[$group]);
		
		// Check if icon exists after filtering
		if ( ! isset( $icons[ $group ][ $icon ] ) ) {
			return false;
		}
		
		// Allow filtering of a single icon
		$svg = apply_filters( "wp_hero_icon_{$group}_{$icon}", $icons[ $group ][ $icon ] );
		
		// Inject the CSS classes and return the SVG
		return sprintf( $svg, esc_attr( $css ) );
